import React, {useState} from "react";
import axios from "axios";

import NewPost from "../../components/home/posts/item/newPost";
import PostItem from "../../components/home/posts/item/postItem";

import "./home.css"

export default function Homepage(){
    const [posts, updatePosts] = useState();

    function requestPosts(){
        axios.post('http://localhost:9000/updatePosts', {
                username: localStorage.getItem('username'),
            }, {withCredentials: true})
            .then((res)=>{
                if(res.status===200){
                  console.log(res.data);
                }
            })
            .catch((err)=>{
                console.log(err);
            });
    }

    
    function renderPosts(){
        return posts.map((post, index) =>
            <PostItem post={post} updatePost={props.update} deletePost={props.delete} id={index}/>
        );
    }


    return(
        <div className="postsContainer">
            <NewPost addPost={props.add}/>
            {renderPosts()}
        </div>);
}
