import React, {useState} from "react";

import "./postItem.css";

export default function NewPost(props){
    
    const [focused, setFocused] = useState(false)
    const [title, setTitle] = useState('')
    const [text, setText] = useState('')
    
    return(
        <div className="postItem">
            <input className="titleContainer" value={focused ? '' : title} placeholder="Enter title"/>
            <textarea className={focused ? "postContentContainer invisible" : "postContentContainer"} placeholder="Enter text"/>
            <div className="postActions">
                <div className="postAction">Cancel</div>
                <div className="postAction">Save</div>
            </div>
        </div>
    );
}
