import axios from 'axios';
import CategoryContainer from "../../components/home/categories/container/categoryContainer";
import PostsContainer from "../../components/home/posts/container/postsContainer";
import "./home.css";
import { array } from "prop-types";

export default function Homepage(){
    // categories is an array of arrays. Each of a child arrays consists of posts
    const [categories, updateCategories] = useState(array());
    // current list of posts
    const [currentCategory, setCategory] = useState();
    // id of a current list. It is used to update this list. categories[categoryId]=updatedCategory
    const [categoryId, setId] = useState(0);

    return(
        <div className="homepage">
            <PostsContainer posts={currentCategory}/>
        </div>
    );
}
