import React from "react";

import PostItem from "../item/postItem";

import "./postsContainer.css"

export default function PostsContainer(props){
    let posts = props.posts;
    function updateCategories(){
        const categories = props.categories;
        const categoryId = props.categoryId;
        categories[categoryId] = posts;
        props.updateCategories(categories);
    }

    function addToEnd(newNote){
        posts.push(newNote);
        updateCategories();
    }

    function updateExisting(id, updatedNote){
        posts[id] = updatedNote;
        updateCategories();
    }
    
    function renderPosts(){
        let id=0;
        return (posts.map((post) =>
            <PostItem post={post} id={id++}/>
        ))
    }

    return(
        <div className="postsContainer">
            <PostItem add={addToEnd} update={updateExisting}/>
            {renderPosts()}
        </div>
        );
}
