import React from "react";
import axios from "axios";

import "./postItem.css";

export default function PostItem(){
    function savePost(){
        axios.post('http://localhost:9000/logout', {
                username: localStorage.getItem('username'),
            }, {withCredentials: true})
            .then((res)=>{
                if(res.status===200){
                  console.log('You are logged out');
                  localStorage.removeItem('username');
                  this.setState({authenticated: false});
                }
            })
            .catch((err)=>{
                console.log(err);
            });
    }
    return(
        <div className="postItem">
            <input className="titleContainer" placeholder="Enter title"/>
            <textarea className="postContentContainer" placeholder="Enter text"/>
            <div className="postActions">
                <div className="postAction">Cancel</div>
                <div className="postAction">Save</div>
            </div>
        </div>
    );
}
