import React from "react";
import PostsContainer from "../../components/home/posts/container/postsContainer";
import "./home.css";

export default class Homepage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            posts:[ {title:'Willy', text:'Nwhnwnhkrl'},
            {title:'hehserhshr', text:'arehaerh'},
            {title:'ahrhawhr', text:'aerhrehehethr'},
            {title:'arhaerha', text:'ahwharwh'}]
        }
    }
    addPost = (post) => {
        
        let newPosts = [...posts];
        newPosts.push(post);
        setPosts(newPosts);
    }

    updatePost = (id, post) =>{
        let newPosts = [...posts];
        newPosts[id] = post;
        setPosts(newPosts);
    }
    
    deletePost = (id) => {
        console.log('Deleting an item with id ' + id);
        const newPosts = this.state.posts;
        newPosts.splice(id, 1);
        this.setState({posts: newPosts});
    }

    render(){
        return(
            <div className="homepage">
                <PostsContainer posts={this.state.posts} add={this.addPost.bind(this)} update={this.updatePost.bind(this)} delete={this.deletePost.bind(this)}/>
            </div>
        );
    }
    
}
