import React, { useState } from "react";
import axios from 'axios';
import CategoryContainer from "../../components/home/categories/container/categoryContainer";
import PostsContainer from "../../components/home/posts/container/postsContainer";
import "./home.css";
import { array } from "prop-types";

export default function Homepage(){
    const [categories, updateCategories] = useState(getPosts());
    const [currentCategory, setCategory] = useState();
    const [categoryId, setId] = useState(0);
    function getPosts(){
        axios.post('http://localhost:9000/getPosts', {
                username: localStorage.getItem('username')
            })
            .then((response)=>{
                if(!response){
                    updateCategories(array());
                }else{
                    setCategory(categories[categories.length-1]);
                }
                console.log(response);
            })
            .catch((err)=>{
                console.log(err);
            });
    }

    return(
        <div className="homepage">
            <CategoryContainer/>
            <PostsContainer Posts={currentCategory}/>
        </div>
    );
}
