import React, { useState } from "react";
import axios from 'axios';
import PostsContainer from "../../components/home/posts/container/postsContainer";
import "./home.css";

export default function Homepage(){
    const [posts, setPosts] = useState([
        {title:'Willy', text:'Nwhnwnhkrl'},
        {title:'hehserhshr', text:'arehaerh'},
        {title:'ahrhawhr', text:'aerhrehehethr'},
        {title:'arhaerha', text:'ahwharwh'},
    ]);

    function addPost(post){
        
        let newPosts = [...posts];
        newPosts.push(post);
        setPosts(newPosts);
    }

    function updatePost(id, post){
        let newPosts = [...posts];
        newPosts[id] = post;
        setPosts(newPosts);
    }
    
    function deletePost(id){
        console.log('Deleting an item with id ' + id);
        let newPosts = posts;
        newPosts.splice(3, 1);
        
        axios.post('http://localhost:9000/updatePosts', {
                username: localStorage.getItem('username'),
                posts: newPosts
            })
            .then((res)=>{
                if(res.status===200){
                    setPosts(res.data);
                }
            })
            .catch((err)=>{
                console.log(err);
            });
    }

    return(
        <div className="homepage">
            <PostsContainer posts={posts} add={addPost.bind(this)} update={updatePost.bind(this)} delete={deletePost.bind(this)}/>
        </div>
    );
}
