import React from "react";
import PostItem from "../../components/home/posts/item/postItem";
import TestItem from "./test";
export default class Homepage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            posts: [
                {
                    title: "Post 1",
                    text: 'Text 1'
                },
                {
                    title: "Post 2",
                    text: 'Text 2'
                },
                {
                    title: "Post 3",
                    text: 'Text 3'
                },
                {
                    title: "Post 4",
                    text: 'Text 4'
                }]
        }
    }

    deleteItem = (id) =>{
        let posts = this.state.posts;
        posts.splice(id, 1);
        this.setState({posts: posts});
    }

    renderItems = (state) =>{
        return state.posts.map((post, index) =>
            <PostItem id={index} post={post} delete={this.deleteItem}/>
        );
    }

    render() {
        return (
             <div>
                 {this.renderItems(this.state)}
             </div>
        );
    }
}
