import React from "react";

import axios from "axios";

import "../../components/authentication/input/field.css";
import "./profile.css";

export default class ProfilePage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            section: 'none',
        }
    }

    changeUsername = (state) =>{
        axios.post('http://localhost:9000/changeUsername', {
            username: state.username,
            newUsername: state.newUsername,
            password: state.password,
        }, {withCredentials: true})
        .then((res)=>{
            if(res.status===200){
                
            }else{
              console.log('Bad cookie');
            }
            
        })
        .catch((err)=>{
            console.log(err);
        });
    }

    renderUsernameChange = (state) =>{
        let returnItem;
        if(state.section === 'username'){
            returnItem = 
            <div className="profileForm">
                <input value={this.state.username} onChange={this.updateUsername} placeholder="Username"/>
                <input value={this.state.username} onChange={this.updateUsername} placeholder="Enter new username"/>
                <input value={this.state.password} onChange={this.updatePassword} placeholder="Password"/>
                <div className="actionDiv doDiv">Change your username</div>
            </div>
        }else{
            returnItem = 
                <div className="actionDiv" onClick={()=>this.setState({section:'username'})}>
                    Change username
                </div>
        }
        return returnItem;
    }
    renderPasswordChange = (state) =>{
        let returnItem;
        if(state.section === 'password'){
            returnItem =
            <div className="profileForm">
                <input value={this.state.username} onChange={this.updateUsername} placeholder="Username"/>
                <input value={this.state.username} onChange={this.updateUsername} placeholder="Password"/>
                <input value={this.state.password} onChange={this.updatePassword} placeholder="Enter new password"/>
                <div className="actionDiv doDiv">Change your password</div>
            </div>
        }else{
            returnItem = 
                <div className="actionDiv" onClick={()=>this.setState({section:'password'})}>
                    Change password
                </div>
        }
        return returnItem;
    }
    renderDeleteAccount = (state) =>{
        let returnItem;
        if(state.section === 'delete'){
            returnItem =
                <div className="profileForm">
                    <input value={this.state.username} onChange={this.updateUsername} placeholder="Username"/>
                    <input value={this.state.username} onChange={this.updateUsername} placeholder="Password"/>
                    <div className="actionDiv warnDiv">Delete your account</div>
                </div>
        }else{
            returnItem = 
                <div className="actionDiv" onClick={()=>this.setState({section:'delete'})}>
                    Delete Account
                </div>
        }
        return returnItem;
    }

    render(){
        return(
            <div className="profilePage">
                {this.renderUsernameChange(this.state)}   
                {this.renderPasswordChange(this.state)}                
                {this.renderDeleteAccount(this.state)}                             
            </div>
        );
    }
}
