import React from "react";
import axios from "axios";

import "./postItem.css";

export default function PostItem(props){
    const post = props.post
    return(
        <div className="postItem">
            <input className="titleContainer" value={props.post.title} placeholder="Enter title"/>
            <textarea className="postContentContainer" value={props.post.text} placeholder="Enter text"/>
            <div className="postActions">
                <div className="postAction" onClick={()=>props.deletePost(props.id)}>Delete</div>
                <div className="postAction" onClick={()=>props.deletePost(props.id)}>Update</div>
            </div>
        </div>
    );
}
