import React from "react";
import '../../components/authentication/input/field.css';
import '../../components/authentication/buttons/authBtns.css';
import './authPage.css';

import axios from "axios";

export default class AuthPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    register = () =>{
        const state = this.state;
        if(state.username&&state.password&&state.confirmPassword){
            console.log("All fields are filled.")
            if(state.password===state.confirmPassword){
                console.log("Passwords match");
                axios.post('http://localhost:9000/register', {
                    username: state.username,
                    password: state.password
                })
                .then((res)=>{
                    console.log(res);
                })
                .catch((err)=>{
                    console.log(err);
                });
            }
        }
    }

    updateUsername = (e) =>{
        this.setState({
            username: e.target.value,
        })
    }

    updatePassword = (e) =>{
        this.setState({
            password: e.target.value,
        })
    }

    updateConfirmPassword = (e) =>{
        this.setState({
            confirmPassword: e.target.value,
        })
    }

    render(){
        return(
            <div className="body">
                <input value={this.state.username} onChange={this.updateUsername} placeholder="Username"/>
                <input value={this.state.password} onChange={this.updatePassword} placeholder="Password"/>
                <input value={this.state.confirmPassword} onChange={this.updateConfirmPassword} placeholder="Confirm Password"/>
                <div className="authBtn" onClick={()=>this.register}>Register</div>
            </div>
        );
    }

}
