import React, { useState } from "react";

import "./postItem.css";

export default function PostItem(props){
    const post = props.post;
    const [title, setTitle] = useState(post.title);
    const [text, setText] = useState(post.text);
    console.log(title + " " + text);
    const [focused, setFocused] = useState(false)
    function onBlur(){
        setTitle(post.title);
        setText(post.text);
        setFocused(false);
    }
    return(
        <div className="postItem existing"
            onFocus={()=>setFocused(true)}
            onBlur={onBlur}>
            <input
                className="titleContainer"
                value={title}
                onChange={e => setTitle(e.target.value)}
                placeholder="Enter title"/>
            <textarea
                className="postContentContainer"
                value={text}
                onChange={e => setText(e.target.value)}
                placeholder="Enter text"/>
            <div className="postActions">
                <div className="postAction" onClick={()=>props.deletePost(props.id)}>Delete</div>
                <div className="postAction" onClick={()=>props.updatePost(props.id, {title:title, text:text})}>Update</div>
            </div>
        </div>
    );
}
