import React, { useState } from "react";
import axios from 'axios';
import PostsContainer from "../../components/home/posts/container/postsContainer";
import "./home.css";

export default function Homepage(){
    const [posts, setPosts] = useState([
        {title:'Willy', text:'Nwhnwnhkrl'},
        {title:'hehserhshr', text:'arehaerh'},
        {title:'ahrhawhr', text:'aerhrehehethr'},
        {title:'arhaerha', text:'ahwharwh'},
    ]);

    function addPost(post){
        
        let newPosts = [...posts];
        newPosts.push(post);
        setPosts(newPosts);
    }

    function updatePost(id, post){
        let newPosts = [...posts];
        newPosts[id] = post;
        setPosts(newPosts);
    }
    
    function deletePost(id){
        console.log('Deleting an item with id ' + id);
        const newPosts = posts.filter(post => post.index != id);

        setPosts(newPosts);
    }

    return(
        <div className="homepage">
            <PostsContainer posts={posts} add={addPost.bind(this)} update={updatePost.bind(this)} delete={deletePost.bind(this)}/>
        </div>
    );
}
